INSERT INTO `posts` (`id`, `title`, `description`, `content`, `viewed`, `votes`, `parent`, `type`, `categorieID`, `userID`, `sttesign`, `price`, `created_at`, `updated_at`) VALUES
(1, 'Cửa gió khuếch tán  V -SAD', 'Cửa gió khuếch tán được sử dụng làm cửa gió cấp, cửa gió hồi', '<h2>Cửa gi&oacute; khuếch t&aacute;n &nbsp;V -SAD</h2>\r\n\r\n<p><img alt=\"\" src=\"http://vitduct.com.vn/storage/app/public/uploads/images/C%E1%BB%ADa%20gi%C3%B3%20khu%E1%BA%BFch%20t%C3%A1n%201_1(1).jpg\" style=\"height:267px; width:400px\" /><img alt=\"\" src=\"http://vitduct.com.vn/storage/app/public/uploads/images/C%E1%BB%ADa%20gi%C3%B3%20khu%E1%BA%BFch%20t%C3%A1n%201_2(1).jpg\" style=\"height:267px; width:400px\" /></p>\r\n\r\n<p><big>* Đặc điểm sử dụng:<br />\r\n- Được sử dụng l&agrave;m của gi&oacute; cấp, của gi&oacute; hồi<br />\r\n- Th&iacute;ch hợp lắp đặt cho tất cả c&aacute;c loại trần giả<br />\r\n- Khung v&agrave; l&otilde;i ri&ecirc;ng biệt, dễ d&agrave;ng th&aacute;o lắp v&agrave; bảo tr&igrave;<br />\r\n- Tuy theo vị&nbsp;tr&iacute; lắp đặt m&agrave; chọn loại thổi&nbsp;1,2,3 hoặc 4 hướng<br />\r\n- C&oacute; thể l&agrave; h&igrave;nh vu&ocirc;ng hoặc h&igrave;nh chữ nhật<br />\r\n* Vật liệu m&agrave;u sắc:<br />\r\n- Khung được l&agrave;m từ nh&ocirc;m định h&igrave;nh tr&ecirc;n c&oacute; g&acirc;n tăng cứng v&agrave; ke g&oacute;c để tạo g&oacute;c vu&ocirc;ng cho cửa.<br />\r\n- C&aacute;nh được l&agrave;m từ nh&ocirc;m định h&igrave;nh,tr&ecirc;n c&aacute;nh c&oacute; g&acirc;n trợ lực để tăng cứng.<br />\r\n- Được sơn tĩnh điện m&agrave;u trắng, m&agrave;u sắc kh&aacute;c theo y&ecirc;u cầu của qu&yacute; kh&aacute;ch h&agrave;ng.</big></p>\r\n\r\n<p><img alt=\"\" src=\"http://vitduct.com.vn/storage/app/public/uploads/images/cua-khuech-tan_result.jpg\" style=\"height:416px; width:1000px\" /></p>\r\n\r\n<p><img alt=\"\" src=\"http://vitduct.com.vn/storage/app/public/uploads/images/cua-khuech-tan-1_result.jpg\" style=\"height:800px; width:1000px\" /></p>\r\n\r\n<p><img alt=\"\" src=\"http://vitduct.com.vn/storage/app/public/uploads/images/cua-khuech-tan-2_result.jpg\" style=\"height:425px; width:1000px\" /></p>', 0, 0, 1, 1, 5, 4, 1, '0', '2016-02-15 06:57:01', '2019-12-13 02:43:47'),
(2, 'Cút góc ống gió: V-DK-A', 'Cút góc ống gió không bọc bảo ôn', '<h1>C&uacute;t g&oacute;c ống gi&oacute;: V-DK-A</h1>\r\n\r\n<p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;<img alt=\"Cút ống gió\" src=\"http://vitduct.com.vn/storage/app/public/uploads/images/cut-ong-gio.png\" style=\"height:400px; width:400px\" /></p>\r\n\r\n<p><img alt=\"cút ống gió\" src=\"http://vitduct.com.vn/storage/app/public/uploads/images/cut-ong-gio_result.jpg\" style=\"height:457px; width:1000px\" /></p>\r\n\r\n<p><big><strong>* Vật liệu m&agrave;u sắc:</strong><br />\r\n&nbsp; &nbsp; Vật liệu phổ biến được d&ugrave;ng để sản xuất <strong>C&uacute;t g&oacute;c ống gi&oacute;</strong> bao gồm: t&ocirc;n mạ kẽm, th&eacute;p đen, nh&ocirc;m, th&eacute;p kh&ocirc;ng gỉ.Tuy nhi&ecirc;n,<br />\r\nc&aacute;c sản phẩm ống gi&oacute; của VlTDUCT phổ biến l&agrave;m từ vật liệu t&ocirc;n mạ kẽm v&agrave; th&eacute;p tẩm kh&ocirc;ng gỉ (SU5201 , SU5304).</big></p>\r\n\r\n<p>&nbsp;</p>', 0, 0, 1, 1, 4, 4, 1, '0', '2016-02-15 07:08:08', '2019-11-03 21:19:11'),
(3, 'Côn ống gió: V-DK-A', 'Côn ống gió vuông tròn', '<h1>C&ocirc;n ống gi&oacute;: V-DK-A</h1>\r\n\r\n<h1>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;<img alt=\"Côn ống gió\" src=\"http://vitduct.com.vn/storage/app/public/uploads/images/cong%20vuong%20tron.png\" style=\"height:348px; width:418px\" /><img alt=\"\" src=\"http://vitduct.com.vn/storage/app/public/uploads/images/Con-thu-vuong-tron.png\" style=\"height:400px; width:400px\" /></h1>\r\n\r\n<p><img alt=\"Côn ống gió\" src=\"http://vitduct.com.vn/storage/app/public/uploads/images/Con-ong-gio_result.jpg\" style=\"height:373px; width:1000px\" /></p>\r\n\r\n<p><big><strong>* Vật liệu m&agrave;u sắc:</strong><br />\r\n&nbsp; &nbsp; &nbsp;Vật liệu phổ biến được d&ugrave;ng để sản xuất <strong>C&ocirc;n&nbsp;ống gi&oacute;</strong>&nbsp;bao gồm: t&ocirc;n mạ kẽm, th&eacute;p đen, nh&ocirc;m, th&eacute;p kh&ocirc;ng gỉ. Tuy nhi&ecirc;n, c&aacute;c sản phẩm ống gi&oacute; của VlTDUCT phổ biến l&agrave;m từ vật liệu t&ocirc;n mạ kẽm v&agrave; th&eacute;p tấm kh&ocirc;ng gỉ (SU5201 , SU5304).</big></p>', 0, 0, 1, 1, 4, 4, 1, '0', '2016-02-15 07:11:45', '2019-11-03 21:19:48'),
(5, 'Ống gió tròn xoắn: V-DK-S', 'Ống gió tròn xoắn được thiết kế bởi những kiến trúc sư giỏi do yêu cầu thẩm mỹ của các công trình, ống có thể được sơn, phối màu phù hợp hoặc nổi bật với cảnh quan bên trong công trình.', '<h1>Ống gi&oacute; tr&ograve;n xoắn: V-DK-S&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</h1>\r\n\r\n<p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;<img alt=\"Ống tròn xoắn\" src=\"http://vitduct.com.vn/storage/app/public/uploads/images/ong-tron-xoan.png\" style=\"height:307px; width:370px\" /></p>\r\n\r\n<p><img alt=\"\" src=\"http://vitduct.com.vn/storage/app/public/uploads/images/ong-gio-tron-xoan_result.jpg\" style=\"height:734px; width:1000px\" /></p>\r\n\r\n<p><img alt=\"\" src=\"http://vitduct.com.vn/storage/app/public/uploads/images/ong-gio-tron-xoan-1_result.jpg\" style=\"height:302px; width:1000px\" /></p>\r\n\r\n<p><span dir=\"ltr\"><big><strong>* Đặc điểm sử dụng:</strong><br />\r\n&nbsp; &nbsp; &nbsp; C&aacute;c sản phẩm <strong>ống gi&oacute; tr&ograve;n xoắn</strong> v&agrave; ống gi&oacute;&nbsp;tr&ograve;n trơn VlTDUCT được chế tạo v&agrave; sản xuất ph&ugrave; hợp với c&aacute;c ti&ecirc;u chuẩn: TCC505:2019|VITDUCT (ban h&agrave;nh lần 2), TCXD 232:1999 v&agrave; c&aacute;c ti&ecirc;u chuẩn quốc tế SMACNA, DW/144 để sử dụng trong c&aacute;c đường ống chịu lực cao, ống dẫn kh&iacute; v&agrave; ống dẫn của hệ thống điều h&ograve;a th&ocirc;ng gi&oacute;. Được sản xuất bởi phương ph&aacute;p đặc biệt l&agrave; mối gh&eacute;p dọc hoặc mối gh&eacute;p 4 chiều, tạo n&ecirc;n cho đường ống độ cứng v&agrave; chịu được &aacute;p lực l&agrave;m việc cao cũng như độ k&iacute;n. Sản phẩm ống gi&oacute; tr&ograve;n xoắn&nbsp;của ch&uacute;ng t&ocirc;i c&ograve;n c&oacute; th&ecirc;m ưu điểm l&agrave; nhẹ, dễ d&agrave;ng trong việc lắp đặt v&agrave; l&agrave;m giảm thiểu tổn thất &aacute;p suất trong qu&aacute; tr&igrave;nh sử dụng.Việc l&agrave;m giảm thiểu tổn thất &aacute;p suất dẫn đến k&iacute;ch thước đường ống được tối ưu h&oacute;a, thuận tiện với điều kiện lắp đặt tại c&aacute;c vị tr&iacute; hạn hẹp như tr&ecirc;n trần thạch cao t&ograve;a nh&agrave;, trong c&aacute;c hộp kỹ thuật. Ch&iacute;nh v&igrave; vậy, việc sử dụng ống gi&oacute; tr&ograve;n&nbsp;xoắn v&agrave; ống gi&oacute; tr&ograve;n trơn thương hiệu VlTDUCT ng&agrave;y c&agrave;ng được phổ biến.</big></span></p>\r\n\r\n<p><span dir=\"ltr\"><big><strong>* Vật liệu m&agrave;u sắc:</strong><br />\r\n&nbsp; &nbsp; &nbsp;Vật liệu phổ biến được d&ugrave;ng để sản xuất <strong>ống gi&oacute;&nbsp;tr&ograve;n xoắn</strong>&nbsp;bao gồm: t&ocirc;n mạ kẽm, th&eacute;p đen, nh&ocirc;m, th&eacute;p kh&ocirc;ng gỉ. Tuy nhi&ecirc;n, c&aacute;c sản phẩm ống gi&oacute; của VlTDUCT phổ biến l&agrave;m từ vật liệu t&ocirc;n mạ kẽm v&agrave; th&eacute;p tấm kh&ocirc;ng gỉ (SU5201 , SU5304).</big></span></p>\r\n\r\n<p><strong><big>* Ti&ecirc;u chuẩn chế tạo:&nbsp;</big></strong></p>\r\n\r\n<p><img alt=\"\" src=\"http://vitduct.com.vn/storage/app/public/uploads/images/ong-gio-tron-xoan-2_result.jpg\" style=\"height:728px; width:1000px\" /></p>\r\n\r\n<p><var><big>* Ghi ch&uacute;: C&oacute; thể được &aacute;p dụng theo c&aacute;c ti&ecirc;u chuẩn tr&ecirc;n hoặc theo y&ecirc;u cầu kỹ thuật hoặc thiết kế của từng dự &aacute;n / y&ecirc;u cầu của kh&aacute;ch h&agrave;ng.</big></var></p>', 0, 0, 1, 1, 4, 4, 1, '0', '2016-02-15 07:13:59', '2019-11-03 21:17:59'),
(7, '4 “ông lớn” bất động sản có ảnh hưởng nhất năm qua', 'Dường như sân chơi lớn đang tập trung trong tay 4 nhà phát triển bất động sản...', '<p></p><p><b>Năm 2015 được coi là một năm thăng hoa của thị trường bất động sản Việt Nam, với số lượng giao dịch thành công tăng đột biến.&nbsp;</b></p><p>Thế nhưng, sự hưng phấn trên toàn thị trường lại không dành cho tất cả các doanh nghiệp trong ngành, khi phần lớn các doanh nghiệp bất động sản nhìn chung vẫn còn nhiều khó khăn trong việc đưa sản phẩm đến tay người tiêu dùng.</p><p>Bất động sản năm 2015 là cuộc đua phân hạng rõ ràng, và nhìn trên góc độ này, dường như sân chơi lớn đang tập trung trong tay 4 nhà phát triển bất động sản.</p><p><img src=\"http://vneconomy2.vcmedia.vn/zoom/500_312/QXDRQqSpnksbgvD7IHAwXMYvXWy4e/Image/2016/01/00-9c384.jpg\"><br></p><p></p><p><strong>Vingroup</strong></p><p>Nếu trước 2015, không khó để liệt kê được các dự án do tập đoàn này triển khai, thì trong năm vừa qua, ngay cả những người theo dõi sát tập đoàn này cũng không thể tự tin nói chính xác số dự án bất động sản mà Vingroup đã khởi công trên khắp cả nước.&nbsp;</p><p>Với Vingroup, các dự án bất động sản được triển khai đều khắp ở nhiều hạng mục như: bất động sản nhà ở thương mại, bất động sản nghỉ dưỡng, trung tâm thương mại.</p><p>Tại Hà Nội, nơi các dự án bất động sản nhà ở của Vingroup tập trung nhiều nhất, tính đến hết năm 2015, tập đoàn này đã sở hữu quỹ đất khổng lồ, với số lượng căn hộ, biệt thự lên tới hàng chục nghìn căn.</p><p>Điều thú vị là, dù chào bán dày đặc, tất cả các dự án đều có quy mô lớn, nhưng dòng tiền thu được từ bán hàng của Vingroup vẫn ở mức rất lớn, với tỷ lệ hấp thụ cao. Đặc biệt hơn, các dự án của Vingroup đều sở hữu một đặc điểm chung là sớm trở thành tâm điểm chú ý của mỗi khu vực khi dự án đi vào hoạt động.</p><p>Không chỉ “ghi điểm” trên địa bàn Hà Nội, tại Tp.HCM, Vingroup cũng đang chứng tỏ ưu thế trong lĩnh vực nhà ở. Cùng với đó là hàng loạt dự án bất động sản nghỉ dưỡng tại các địa phương trên cả nước. Đối với phân khúc bất động sản, Vingroup không chỉ tạo ra những quần thể nghỉ dưỡng lớn, đẳng cấp, mà các sản phẩm nghỉ dưỡng thuộc các quần thể này cũng có sức hấp dẫn đáng kể với thị trường.</p><p>Triển khai nhiều dự án, các dự án có quy mô lớn, ở đẳng cấp khá cao, luôn tạo ra dấu ấn và đặc biệt là được thị trường đón nhận ở cả phân khúc nghỉ dưỡng lẫn nhà ở thương mại là đặc điểm nổi bật của Vingroup.&nbsp;</p><p>Hiện tại, cách bán hàng của Vingroup liên quan đến cam kết lợi nhuận tại các dự án bất động sản nghỉ dưỡng đã được nhiều doanh nghiệp học theo, nhưng làm được như Vingroup lại không hề đơn giản, bởi cái thị trường còn cần là uy tín - điều các chủ đầu tư lại không dễ làm được trong ngày một ngày hai.</p><p></p><p><b>FLC Group</b></p><p>Tại Hà Nội, trong vòng một năm qua, số dự án khởi công, mở bán rất nhiều. Và ngoài Vingroup, trong năm 2015, trong khi nhiều doanh nghiệp bất động sản khác vẫn chật vật bán hàng, thì với hơn 4.000 căn hộ và biệt thự ra mắt thị trường trong năm 2015,&nbsp;FLC dường như là doanh nghiệp duy nhất có sản phẩm chào bán “cháy hàng” ngay trong ngày đầu tiên mở bán.</p><p>Ở phân khúc nhà ở thương mại, các dự án của FLC hầu hết là các dự án riêng lẻ, nhưng mang đặc điểm chung là có vị trí đẹp, nằm ở khu vực có tỷ lệ hấp thụ thuộc top cao nhất của Hà Nội, như các dự án FLC Complex 36 Phạm Hùng, FLC Garden City, FLC Greenhome... Ngay dự án tháp đôi FLC Twin Towers tại 265 Cầu Giấy, chưa cần phải chào bán, FLC đã có lượng đăng ký đặt mua xấp xỉ số căn dự kiến sẽ chào bán.</p><p>Hiện tại, ngoài những dự án chung cư đã công bố, FLC còn sở hữu hàng loạt dự án khác tại Hà Nội, với tổng số hơn 10 tòa căn hộ cùng nhiều dự án nhà liền kề, biệt thự... với một đặc điểm giống như các dự án đã công bố, là đều được thị trường quan tâm, nhiều đơn vị trung gian đăng ký mua lại.</p><p>Ngoài mảng nhà ở thương mại tại Hà Nội, tương tự Vingroup, FLC cũng sở hữu các quần thể dự án nghỉ dưỡng tại các địa phương khác, với tổng mức đầu tư lên tới hàng chục nghìn tỷ đồng như: Thanh Hóa (FLC Sầm Sơn Beach &amp; Golf Resort), Bình Định (FLC Quy Nhơn Beach &amp; Golf Resort), Vĩnh Phúc (FLC Vĩnh Thịnh Resort), quần thể sân golf tại Quảng Bình, sắp tới là Quảng Ninh... chưa kể dự án nhà ở tại Thanh Hóa.</p><p>Ở phân khúc này, FLC đã tạo được dấu ấn cho chính mình và địa phương, khi trở thành người đi đầu biến tiềm năng du lịch tại các địa phương trở thành hiệu quả kinh tế cho doanh nghiệp và địa phương. Với FLC Sầm Sơn, chỉ trong một thời gian ngắn đi vào hoạt động, quần thể dự án này đã thu hút hàng chục nghìn lượt khách, trở thành tâm điểm du lịch của Thanh Hóa, tạo được doanh thu khá lớn khi hàng trăm biệt thự đã được thị trường tiếp nhận ngay lần đầu tiên mở bán.</p><p><span style=\"line-height: 1.45em; background-color: initial;\">FLC cũng đang từng bước “cắm mốc” tại các địa danh giàu tiềm năng dọc theo dải bờ biển miền Trung. Chỉ trong vòng hai năm qua, hàng tỷ USD đã được tập đoàn này rót vào các dự án bất động sản nghỉ dưỡng tại Thanh Hóa, Bình Định và tới đây sẽ là Quảng Bình, Quảng Ninh.</span><br></p><p><b>Novaland</b></p><p>Nếu ở phía Bắc, Vingroup được coi là người thống lĩnh thị trường bất động sản nhà ở, thì tại khu vực miền Nam, Novaland cũng là cái tên được nhắc đến gần đây, với lượng sản phẩm bán ra khổng lồ trong vòng 3 năm nay, cùng quỹ dự án lớn.</p><p>Với hàng chục dự án trải khắp các địa bàn Tp.HCM, như Sunrise City, Suriseview, Sunrise Riverside, Golden Mansion, Orchard Parkview, Newton Residence, Duxton Residence, The Sun Avenue, The Botanica, RiverGate, Lucky Palace, Lucky Dragon... khiến khách hàng tại thị trường này bắt đầu rơi vào cảm giác “ra ngõ gặp Novaland”.</p><p>Ở khu vực phía Nam, thị trường không rơi vào tình trạng phân hạng mạnh như khu vực phía Bắc, nhưng phía sau Novaland, các doanh nghiệp bất động sản khác dù vẫn bán hàng được, nhưng tốc độ phát triển chậm hơn, và ở quy mô nhỏ hơn rất nhiều.</p><p><b>Sun Group</b></p><p>Ở mảng bất động sản nghỉ dưỡng, ngoài Vingroup, FLC, năm 2015, Sun Group là cái tên được thị trường biết đến với hàng loạt dự án đầu tư vào Đà Nẵng (Bà Nà Hills, InterContinental Sun Peninsula Resort...).</p><p>Tại Quảng Ninh, Sun Group cũng đang đầu tư mạnh với hệ thống cáp treo xuyên vịnh Hạ Long. Hệ thống cáp treo này nằm trong tổ hợp dự án 6.000 tỷ đồng bao gồm một quần thể công viên trên núi, khu vui chơi giải trí trên đồi, vòng quay khổng lồ, và một thủy cung lớn phía dưới chân bờ vịnh Hạ Long.</p><p>Ngoài ra, tập đoàn này cũng đang và sẽ đầu tư vào các địa phương khác như Lào Cai, Phú Quốc.</p><p><span style=\"line-height: 1.45em; background-color: initial;\">Với lượng khách khổng lồ đã đến với Sun Group trong suốt các năm qua, doanh nghiệp này đang chứng tỏ vị thế ngày một lớn của mình trong lĩnh vực bất động sản nghỉ dưỡng, với một hướng đầu tư rất riêng tại Việt Nam.</span><br></p><p></p><p></p><p></p>\r\n', 0, 0, 3, 0, 3, 1, 1, '0', '2016-02-15 07:31:04', '2016-02-15 07:38:11'),
(8, 'Cứ 10 giây, Apple kiếm được 11.740 USD lợi nhuận', 'Một mình Apple chiếm đến hơn nửa lợi nhuận của các công ty công nghệ lớn nhất thế giới... Không còn nghi ngờ gì nữa, yếu tố lớn nhất tác động đến cuộc sống hiện đại chính là khả năng sao lưu và dự trữ dữ liệu với chi phí bằng không. ', '<p><p><strong>Không còn nghi ngờ gì nữa, yếu tố lớn nhất tác động đến cuộc sống hiện đại chính là khả năng sao lưu và dự trữ dữ liệu với chi phí bằng không. </strong></p><p>Cuộc cách mạng về thông tin đã mang đến cho con người rất nhiều lợi ích mà không phải tốn quá nhiều chi phí.</p><p>Giờ đây người ta có thể kết nối với kho kiến thức toàn cầu mà không phải trả một xu nào hoặc số tiền phải trả quá thấp đến mức không cần thiết phải bận tâm.</p><p>Những ứng dụng mới với nhiều tính năng ấn tượng có giá chưa đến một USD hay giá thuê bao dịch vụ truyền hình trực tuyến Netflix quá thấp đến mức hiếm khi chúng ta bận tâm đến nó khi thanh toán nợ thẻ tín dụng. </p><p>Đó là khi tiếp nhận thông tin còn khi muốn chia sẻ thông tin lại với cộng đồng, chúng ta cũng chẳng phải trả bất kỳ khoản tiền nào thông qua mạng xã hội, email hay nhiều phương tiện khác.</p><p>Trong suốt lịch sử loài người, chưa bao giờ con người được hưởng nhiều tiện ích như trên. Người ta chỉ có thể làm được điều đó nhờ vào sự phát triển bùng nổ của mạng Internet.</p><p>Có bao giờ bạn tự hỏi bạn thực hiện bao nhiêu thao tác tìm kiếm trên Google mỗi ngày? Bạn bè của bạn tìm kiếm trên Google bao nhiêu lần một ngày? </p><p><img src=\"http://vneconomy2.vcmedia.vn/zoom/500_312/S8igPoDlP2Gama5joBTW45D1IvpL1/Image/2016/02/loinhuan-88a79.png\"><br></p><p>Theo số liệu của Business Insider, cứ mỗi 10 giây trôi qua, khoảng 225.740 GB dữ liệu được chuyển tải trên mạng Internet. </p><p>Cũng trong khoảng thời gian trên, 46 nghìn lệnh tìm kiếm được thực hiện trên Google, 110 tài khoản twitter được tạo ra, trên Facebook có thêm 549.760 nội dung mới được đăng tải; 34.027.780 email được gửi đi, 2 triệu tin nhắn được trao đổi trên ứng dụng WhatsApp hay 6.340 ứng dụng trên App Store được tải về. </p><p>Mỗi một thao tác của bạn trên mạng Internet chỉ mang lại vài cent rất nhỏ cho nhà cung cấp ứng dụng nhưng tính theo con số rất lớn các thao tác như trên, nhà sản xuất thu được món lợi không hề nhỏ.</p><p>Vậy các công ty công nghệ lớn của thế giới kiếm được bao nhiêu tiền khi cứ mỗi 10 giây qua đi?</p><p>Theo thứ tự như trên, cứ mỗi 10 giây, Google kiếm được 4.090 USD lợi nhuận; Twitter thu được 200 USD doanh thu nhưng lại đang lỗ 200 USD; Facebook kiếm được 480 USD lợi nhuận; Apple thu về 11.740 USD lợi nhuận.</p><p>Và nếu tính chung, con số lợi nhuận chắc chắn sẽ khiến nhiều người ngạc nhiên. Cứ mỗi 10 giây trôi qua, các công ty công nghệ trên thu về 23.630 USD lợi nhuận và khoảng một nửa con số trên về túi của Apple. </p></p>', 0, 0, 3, 0, 3, 1, 1, '0', '2016-02-15 07:40:00', '2016-03-16 02:12:12'),
(9, 'Facebook cán ngưỡng 1 tỷ USD lợi nhuận', 'Facebook ngày 27/1 lần đầu tiên công bố mức lợi nhuận ròng hàng quý vượt mức 1 tỷ USD, cho thấy khả năng của mạng xã hội lớn nhất hành tinh trong việc kiếm tiền nhanh từ mức độ phổ biến ngày càng rộng rãi của mình.', '<p><strong>Facebook ngày 27/1 lần đầu tiên công bố mức lợi nhuận ròng hàng quý vượt mức 1 tỷ USD, cho thấy khả năng của mạng xã hội lớn nhất hành tinh trong việc kiếm tiền nhanh từ mức độ phổ biến ngày càng rộng rãi của mình.</strong><br><br>Tờ Wall Street Journal cho biết, doanh thu quý 4/2015 của Facebook tăng 52% so với cùng kỳ năm trước, đạt mức 3,85 tỷ USD nhờ lượng khách hàng quảng cáo tăng mạnh. Trong khi đó, chi phí chỉ tăng 21%.<br><br>Giá cổ phiếu của Facebook đã tăng 4,3% trong phiên giao dịch điện tử tại thị trường New York sau khi kết quả kinh doanh khả quan trên được công bố.</p><p><img src=\"http://vneconomy2.vcmedia.vn/zoom/500_312/e8MGt3z9mEZXVBKvj6NiUjxtCZ14Zf/Image/2016/01/00-1817c.jpg\"><br><br>Năm 2015 là năm mà Facebook đầu tư mạnh để phát triển. Công ty của Mark Zuckerberg đã thuê thêm hàng nghìn nhân viên và rót tiền vào những dự án như kính thực tế ảo Oculus VR, trí tuệ nhân tạo, và phủ sóng Internet tới các khu vực xa xôi.<br><br>Ngoài ra, Facebook cũng mở rộng thêm lựa chọn cho khách hàng quảng cáo, chẳng hạn cho khách hàng đặt thêm quảng cáo trên ứng dụng chia sẻ ảnh Instagram.&nbsp;<br><br>Trong nhiều trường hợp, Facebook có thể đòi mức giá cao hơn cho những quảng cáo như vậy, đặc biệt là ở thị trường Bắc Mỹ. Theo công ty nghiên cứu Bernstein Research, giá quảng cáo trên bảng tin (news feed) của Facebook tại Bắc Mỹ tăng tới 20% trong năm ngoái.<br><br>Doanh thu và lợi nhuận tăng đẩy giá cổ phiếu Facebook tăng gần 30% trong năm ngoái. Có thời điểm, giá trị vốn hóa thị trường của công ty 12 năm tuổi này vượt mức 300 tỷ USD.<br><br>Lợi nhuận ròng quý 4 của Facebook đạt 1,56 tỷ USD, tương đương 0,54 USD/cổ phiếu, từ mức 701 triệu USD, tương 0,25 USD/cổ phiếu cùng kỳ năm ngoái.<br><br>Trong quý 4, số người truy cập Facebook ít nhất 1 lần trong 1 tháng đạt 1,59 tỷ người, từ mức 1,55 tỷ người trong quý 3.<br></p>', 0, 0, 3, 0, 3, 1, 1, '0', '2016-02-15 07:46:33', '2016-02-15 07:46:33'),
(10, 'Áp thuế giá thép tăng vụt, có tích trữ đầu cơ?', '“Đang nhức đầu với...cơ quan công an vì xe tải nối đuôi chờ lấy hàng nhà máy dưới Bà Rịa - Vũng Tàu. Dù nỗ lực giao hàng cho các tài xế chở hàng nhưng số phiếu xuất hàng rất nhiều, lại tập trung đông hơn mức bình thường nên vẫn còn xe chờ bên ngoài”.', '<p><p>Sáng 13-3, trao đổi với&nbsp;<em>Tuổi Trẻ</em>, ông Đỗ Duy Thái, chủ tịch HĐQT Công ty CP Thép Việt (Pomina), xác nhận: “Đang nhức đầu với...cơ quan công an vì xe tải nối đuôi chờ lấy hàng nhà máy dưới Bà Rịa - Vũng Tàu. Dù nỗ lực giao hàng cho các tài xế chở hàng nhưng số phiếu xuất hàng rất nhiều, lại tập trung đông hơn mức bình thường nên vẫn còn xe chờ bên ngoài”.</p><p><img src=\"http://static.new.tuoitre.vn/tto/i/s626/2016/03/14/6f763c82.jpg\" style=\"\"><br></p><p style=\"text-align: center;\"><i>Tài xế ngồi chờ lấy phiếu để nhận thép tại huyện Tân Thành, Bà Rịa - Vũng Tàu - Ảnh: Đông Hà</i><br></p><p>Theo ông Thái, sở dĩ có tình trạng các đại lý, nhà phân phối điều nhà xe đến lấy thép nhiều hơn một cách bất thường là do quyết định áp thuế tự vệ tạm thời của Bộ Công thương ban hành hôm 7-3 (mức 23,3% đối với phôi thép, 14,2% đối với thép dài dưới dạng thuế nhập khẩu bổ sung, có hiệu lực từ ngày 22-3 tới). Thuế tăng, một số người suy đoán giá sau ngày 21-3 sẽ cao hơn so với thời điểm hiện nay nên đổ xô đi mua.</p><p>Ông Thái cũng không phủ nhận việc Pomina vừa điều chỉnh giá bán thép các loại giao tại nhà máy đã tăng thêm 250.000 đồng/tấn kể từ ngày 9-3, tức hai ngày sau khi Bộ Công thương ban hành quyết định nói trên, từ 8,7 triệu đồng/tấn lên 8,95 triệu đồng/tấn (chưa VAT). Dự kiến từ ngày 14-3, Pomina sẽ tiếp tục điều chỉnh giá bán.</p><p>Cũng có đoàn xe xếp hàng dài chờ lấy thép tại nhà máy ở Bà Rịa - Vũng Tàu nhưng ông Nguyễn Đình Phúc, tổng giám đốc Công ty TNHH một thành viên Thép Miền Nam (VNSteel), cho rằng không phải là vì mức thuế tăng của Bộ Công thương, mà do sự tình cờ trùng hợp một số nguyên liệu sản xuất thép như thép phế, phôi thép, quặng... đều có xu hướng nhích giá sau một thời gian dài giảm giá.</p><p>Với nguồn cung khoảng 40.000 tấn/tháng như hiện nay, ông Phúc khẳng định đoàn xe đến lấy hàng chủ yếu là các đại lý, nhà phân phối đến chở hàng theo hợp đồng đã ký với VNSteel. “Phần lớn các nhà phân phối đều có hợp đồng dài hạn với các công trình xây dựng nên họ phải cam kết giữ giá và cần sự ổn định về nguồn hàng cung cấp.</p><p>Chúng tôi vẫn xuất hàng bán bình thường, nguồn cung sản xuất thép vẫn vượt cầu nên không có chuyện không có hàng, hoặc ghim hàng từ nhà sản xuất. Vấn đề là mọi người cùng kéo đến một lúc thì nó sẽ đông hơn so với bình thường” - ông Phúc giải thích.</p><p>Dù các doanh nghiệp giải thích như vậy nhưng ông Nguyễn Văn Sưa, phó chủ tịch Hiệp hội Thép VN (VSA), cho rằng thị trường tăng giá ào ạt là vô lý bởi năng lực sản xuất thép trong nước đang vượt rất xa nhu cầu. “Thị trường sẽ điều tiết theo nhu cầu thật của nó, chứ không thể tác động bởi các nhu cầu hoặc thông tin ảo được” - ông Sưa nói.</p><p>Theo tìm hiểu của&nbsp;<em>Tuổi Trẻ</em>, trong tháng 1 và 2-2016, tổng lượng thép xây dựng tiêu thụ trên thị trường xấp xỉ 1,1 triệu tấn, tăng lần lượt 36,6 - 90% so với cùng kỳ năm 2015, bất chấp đây là tháng cận tết và trong tết. Nếu cộng thêm lượng thép tồn kho tại các doanh nghiệp (tức chưa bán được) tính đến ngày 29-2 xấp xỉ 500.000 tấn nữa, nên việc thiếu thép rất khó xảy ra.</p><p>Ông Sưa cho rằng việc một số doanh nghiệp điều chỉnh giá bán với lý do giá nguyên liệu tăng chỉ có thể chấp nhận được nếu nguyên liệu đó cập cảng VN từ tháng 4 trở đi. Còn nếu vẫn sử dụng các nguyên liệu giá thấp trước đã mua trong các tháng cuối năm 2015, hay thậm chí đầu tháng 1-2016 mà vẫn tăng giá theo kiểu đón đầu thì chưa hợp lý.</p><p>Tình trạng&nbsp;<a href=\"http://tuoitre.vn/tin/kinh-te/20160312/chuyen-la-ket-xe-tai-cho-nhan-thep/1066123.html\">hàng đoàn xe tải ùn ùn</a>&nbsp;xếp hàng dài chờ đến lượt lấy hàng tại các nhà máy thì không ít doanh nghiệp sản xuất thép cho rằng do chính sách tăng thuế của Bộ Công thương mà ra. Còn các chuyên gia thị trường lại khẳng định tâm lý tích trữ kéo theo đầu cơ mới là nguyên nhân thật sự.</p></p>', 0, 0, 3, 0, 3, 1, 1, '0', '2016-03-16 02:23:05', '2016-03-16 02:23:05'),
(12, 'Van tròn điều chỉnh lưu lượng loại tay gạt  V-VCD/C/H', 'Van điều chỉnh lưu lượng tròn (VD)', '<h1>Van tr&ograve;n điều chỉnh lưu lượng loại tay gạt &nbsp;V-VCD/C/H</h1>\r\n\r\n<p><img alt=\"\" src=\"http://vitduct.com.vn/storage/app/public/uploads/images/Van%20VCD%20tr%C3%B2n%201_1.JPG\" style=\"height:267px; width:400px\" />&nbsp; &nbsp;&nbsp;<img alt=\"\" src=\"http://vitduct.com.vn/storage/app/public/uploads/images/Van%20VCD%20tr%C3%B2n%201_2.jpg\" style=\"height:267px; width:400px\" /></p>\r\n\r\n<p><strong><big>* Đặc điểm sử dụng:</big></strong></p>\r\n\r\n<ul>\r\n	<li><big>D&ugrave;ng&nbsp;để điều chỉnh lưu lượng gi&oacute; cấp v&agrave; gi&oacute; hồi.</big></li>\r\n	<li><big>Được&nbsp;lắp trực tiếp tr&ecirc;n đường ống cấp hoặc ống hồi ph&iacute;a sau hộp g&oacute;p gi&oacute;.</big></li>\r\n	<li><big>Việc điều chỉnh g&oacute;c mở của c&aacute;nh van được thực hiện bằng tay qua&nbsp;trục v&iacute;t,</big>&nbsp;<big>hoặc điều chỉnh tự động bằng loại van gắn động cơ.</big></li>\r\n</ul>\r\n\r\n<p><big><strong>* Vật liệu m&agrave;u sắc:</strong></big></p>\r\n\r\n<ul>\r\n	<li><big>Được l&agrave;m bằng t&ocirc;n mạ kẽm hoặc inox.</big></li>\r\n	<li><big>Tay van c&oacute; thể l&agrave;m bằng nhựa,tay b&aacute;nh v&iacute;t hoặc tay gạt.</big></li>\r\n	<li><big>Rất dễ d&agrave;ng cho việc điều chỉnh lưu lượng gi&oacute; cấp v&agrave; gi&oacute; hồi</big></li>\r\n	<li><big>Tay van: Tay gạt.</big></li>\r\n</ul>\r\n\r\n<p><img alt=\"\" src=\"http://vitduct.com.vn/storage/app/public/uploads/images/Van-tron-dieu-chinh-luu-luong-loai-tay-gat_result.jpg\" style=\"height:874px; width:1000px\" /></p>\r\n\r\n<p><img alt=\"\" src=\"http://vitduct.com.vn/storage/app/public/uploads/images/Van-tron-dieu-chinh-luu-luong-loai-tay-gat-1_result.jpg\" style=\"height:407px; width:1000px\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>', 0, 0, 1, 1, 7, 4, 1, '0', '2016-04-03 07:05:22', '2019-11-04 00:02:33'),
(13, 'Giới thiệu về công ty', 'Với mục tiêu mở rộng thị trường, nâng cao năng lực trong các hoạt động xây dựng, để thực hiện các dự án xây dựng các công trình trong lĩnh vực công nghiệp luyện kim, dân dụng, …', '<p>Kính thưa quí khách hàng.</p><p>Công ty cổ phần Đầu tư xây lắp và thương mại Việt Nam được thành lập năm 2011. Với tôn chỉ hoạt động lấy sự hài lòng của khách hàng làm mục đích, lấy chất lượng của công trình làm thước đo cho sự phát triển của Công ty. Chính vì phương châm đó, toàn thể cán bộ công nhân viên công ty không ngừng nâng cao kiến thức, tay nghề...ngày càng hoàn thiện, để phục vụ khách hàng được tốt hơn. Năng lực của người lao động đươc đề cao và phát huy tối đa để tạo ra chất lượng của sản phẩm và chất lượng của các dự án mà công ty đã, đang và tiến tới tham gia.&nbsp;</p><p>Công ty cổ phần Đầu tư xây lắp và thương mại Việt Nam, ngành nghề và lĩnh vực hoạt động chính của công ty: Sản xuất vật tư cho hệ thống thông gió, lò xo chống rung giảm chấn và cung cấp, thi công các hệ thống cơ điện trong xây dựng dân dụng và công nghiệp. Mục tiêu trong tương lai gần có thể đứng vai trò làm tổng thầu M&amp;E trong các dự án lớn. Để thực hiện mục tiêu trên công ty không ngừng mở rộng về qui mô kinh doanh và qui mô đầu tư về máy móc trang thiết bị hiện đại để nâng cao chất lượng sản phẩm, hạ giá thành và nâng cao sức cạnh tranh.</p><p>Ngoài các yếu tố về đầu tư để mở rộng qui mô, sản xuất thêm các mặt hàng mới phục vụ thị trường. Công ty còn mở rộng sự liên doanh liên kết các hãng, nhà máy cung cấp thiết bị, vật tư trong ngành cơ điện. Với mong muôn đêm tới cho quí khách hàng sự hài lòng nhất. Chính sự thành công của quí khách hàng, là sự thành công của chúng Tôi.</p><p>Rất mong nhận được sự quan tâm và hợp tác.</p><p>Trân trọng cảm ơn!</p>', 0, 0, 16, 3, 17, 3, 1, '0', '2016-04-06 04:33:55', '2016-04-20 23:10:08'),
(17, 'Dự án Golden Palace - Đường Mễ Trì - Quận Nam Từ Liêm - Hà Nội', 'Dự án Golden Palace - Đường Mễ Trì là dự án tổ hợp Văn phòng dịch vụ và căn hộ cao cấp, do công ty CP Đầu tư Mai Linh là chủ đầu tư. Dự án gồm 3 tòa tháp cao 31 tầng nổi và 3 tầng hầm.\r\nCông ty tham gia cung cấp và thi công toàn bộ hệ thống điều hòa không khí của tháp B, thông gió toàn bộ phần khối đế và thông gió tầng hầm', '<p><p></p><p><img src=\"http://vitduct.com.vn/uploads/0416/8017Golden Palace 2.jpg\"></p><br></p>', 0, 0, 2, 4, 10, 3, 1, '0', '2016-04-11 16:24:44', '2016-04-11 16:24:44'),
(18, 'Dự án Golden Palace C3 Lê Văn Lương', 'Dự án Golden Palace C3 Lê Văn Lương', '<p><p><img src=\"http://vitduct.com.vn/uploads/0416/9792C3.jpg\" style=\"\"></p>Dự án Golden Palace C3 Lê Văn Lương là tổ hợp Dịch vụ và căn hộ cao cấp do công ty TNHH ĐTXD Hoàng Cường làm chủ đầu tư. Dự án bao gồm &nbsp;3 tầng hầm và 21 tầng nổi.</p><p>Công ty VIT tham gia cung cấp và thi công Hệ thống điều hòa không khí và thông gió</p>', 0, 0, 2, 4, 10, 3, 1, '0', '2016-04-11 16:29:25', '2016-04-11 16:29:25'),
(19, 'Nhà máy lọc hóa dầu Nghi Sơn', 'Nhà máy lọc hóa dầu Nghi Sơn', '<p><p><img src=\"http://vitduct.com.vn/uploads/0416/6753Loc dau Nghi Son.jpg\" style=\"\"></p>Dự án nhà máy lọc hóa dầu Nghi Sơn, là nhà máy lọc hóa dầu lớn nhất của Việt Nam tại thời điểm hiện tại, với công nghệ hiện đại và do tổ hợp liên doanh nhà thầu JGCS thực hiện.</p><p>Công ty VIT tham gia cung cấp và thi công các hạng mục cơ điện: Điều hòa không khí, FM200, LAN/TEL, báo cháy, điện và âm thanh.</p>', 0, 0, 2, 4, 10, 3, 1, '0', '2016-04-11 16:33:47', '2016-04-11 16:33:47'),
(21, 'Trụ sở công ty 319 - 63 Lê Văn Lương', 'Trụ sở công ty 319 - 63 Lê Văn Lương', '<p><p><img src=\"http://vitduct.com.vn/uploads/0416/2545TRU SO CONG TY 319 - 63 Le Van Luong.jpg\" style=\"\"></p>Dự ánTrụ sở công ty 319 - 63 Lê Văn Lương, do uTổng công ty 319 BQP làm chủ đầu tư<p>Công ty VIT tham gia cung cấp và thi công Hệ thống điều hòa không khí và thông gió</p><br></p>', 0, 0, 2, 4, 10, 3, 1, '0', '2016-04-11 16:43:41', '2016-04-11 16:43:41'),
(22, 'Dự án Thăng Long Number 1', 'Dự án Thăng Long Number 1', '<p><p><img src=\"http://vitduct.com.vn/uploads/0416/998thang-long-number-one4.jpg\" style=\"\"></p>Dự án Thăng Long Number 1<br></p><p>Công ty VIT tham gia cung cấp và thi công Hệ thống thông gió<br></p>', 0, 0, 2, 4, 10, 1, 1, '0', '2016-04-11 16:47:09', '2016-04-11 16:48:35'),
(25, 'Ống gió mềm các loại', 'Ống gió mềm các loại', '<p><img src=\"http://vitduct.com.vn/uploads/0416/4645ong gio mem.jpg\" style=\"line-height: 1.45em; background-color: initial;\"><br></p><p><p><p>Ống gió mềm các loại được phân thành 2 loại chính:</p><p>1. Loại có bảo ôn: Được dùng cho hệ thống cấp và hồi gió lạnh trong hệ thống điều hòa và mục đích chống cháy.</p><p>Các kích thước cơ bản như sau: DN100, DN150, DN200, DN250, DN300 và DN350</p><p>2. Loại không có bảo ôn: Được dùng cho hệ thống cấp và hồi khí thải và cấp gió tươi.</p></p><p>Các kích thước cơ bản như sau: DN100, DN150, DN200, DN250, DN300 và DN350</p><br></p>', 0, 0, 1, 1, 4, 3, 1, '0', '2016-04-11 17:01:32', '2016-04-11 17:01:32'),
(27, 'Cửa gió hồi nan thẳng có lưới lọc bụi: V-FRG-F', 'Cửa gió hồi 2 khung có bản lề', '<h2>Cửa gi&oacute; hồi nan thẳng c&oacute; lưới lọc bụi: V-FRG-F</h2>\r\n\r\n<p><img alt=\"\" src=\"http://vitduct.com.vn/storage/app/public/uploads/images/C%E1%BB%ADa%20gi%C3%B3%20nan%20b%E1%BA%A7u%20d%E1%BB%A5c%201%20l%E1%BB%9Bp%202%20khung%20c%C3%B3%20l%C6%B0%E1%BB%9Bi%20l%E1%BB%8Dc%201_1.jpg\" style=\"height:267px; width:400px\" /><img alt=\"\" src=\"http://vitduct.com.vn/storage/app/public/uploads/images/C%E1%BB%ADa%20gi%C3%B3%20nan%20b%E1%BA%A7u%20d%E1%BB%A5c%201%20l%E1%BB%9Bp%202%20khung%20c%C3%B3%20l%C6%B0%E1%BB%9Bi%20l%E1%BB%8Dc%201_2.jpg\" style=\"height:267px; width:400px\" /></p>\r\n\r\n<p><strong>* Đặc điểm sử dụng</strong></p>\r\n\r\n<p>- Được sử dụng l&agrave;m <a href=\"http://vitduct.com.vn/ag==/cua-gio\">cửa gi&oacute;</a> hồi</p>\r\n\r\n<p>- Được lắp đặt tr&ecirc;n trần giả hoặc lắp trực tiếp tr&ecirc;n đường ống.</p>\r\n\r\n<p>- C&oacute; gắn k&egrave;m theo&nbsp;lưới lọc bụi ( th&ecirc;m k&yacute; hiệu F) hoặc kh&ocirc;ng c&oacute; lưới lọc bụi ( kh&ocirc;ng c&oacute; k&yacute; hiệu F), đặc biệt l&agrave; cửa c&oacute; cơ cấu bản lề rất thuận tiện cho việc vệ sinh lưới định lỳ hoặc kh&ocirc;ng bao gồm bản lề...</p>\r\n\r\n<p>- C&aacute;c c&aacute;nh c&oacute; thể l&agrave;m bằng nan thẳng hoặc nan cong, điều ch&igrave;nh với g&oacute;c độ t&ugrave;y theo y&ecirc;u cầu sử dụng.</p>\r\n\r\n<p><strong>* Vật liệu v&agrave; m&agrave;u sắc&nbsp;</strong></p>\r\n\r\n<p>- Khung được l&agrave;m từ nh&ocirc;m định h&igrave;nh d&agrave;y , tr&ecirc;n c&oacute; g&acirc;n tăng cứng v&agrave; ke g&oacute;c để tạo g&oacute;c vu&ocirc;ng cho to&agrave;n bộ cửa.</p>\r\n\r\n<p>- C&aacute;nh được l&agrave;m từ nh&ocirc;m định h&igrave;nh</p>\r\n\r\n<p>- Được sơn bằng d&acirc;y chuyền sơn tĩnh điện m&agrave;u sắc, hoặc m&agrave;u sắc kh&aacute;c theo y&ecirc;u cầu của Kh&aacute;ch h&agrave;ng.</p>\r\n\r\n<p>- Lưới lọc bụi được l&agrave;m từ vật li&ecirc;u lọc bụi chuy&ecirc;n dụng, hiện quả lọc cao.</p>\r\n\r\n<p><img alt=\"\" src=\"http://vitduct.com.vn/storage/app/public/uploads/images/cua-gio-hoi-nan-thang-co-luoi-loc-bui_result.jpg\" style=\"height:903px; width:993px\" /></p>\r\n\r\n<p><img alt=\"\" src=\"http://vitduct.com.vn/storage/app/public/uploads/images/cua-gio-hoi-nan-thang-co-luoi-loc-bui-1_result.jpg\" style=\"height:1200px; width:969px\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>', 0, 0, 1, 1, 5, 4, 1, '0', '2016-04-11 17:10:11', '2019-12-13 02:55:42'),
(29, 'Cửa gió kiểu khe Slot: V-SLD', 'Cửa gió cấp, hồi dạng Slot', '<h2>Cửa gi&oacute; kiểu khe Slot: V-SLD</h2>\r\n\r\n<p><img alt=\"\" src=\"http://vitduct.com.vn/storage/app/public/uploads/images/cua-gio-kieu-khe-slot-2_result.jpg\" style=\"height:194px; width:800px\" /></p>\r\n\r\n<p><strong>* Đặc điểm sử dụng:</strong></p>\r\n\r\n<p>- Cửa gi&oacute; dạng khe hẹp của VITDUCT được thiết kế đặc biệt cho c&ocirc;ng tr&igrave;nh với t&iacute;nh thẩm mỹ cao như sảnh kh&aacute;ch sạn, ph&ograve;ng ngủ cao cấp. Thường được d&ugrave;ng với hệ thống thống gi&oacute; - cấp nhiệt, hệ thống điều h&ograve;a &acirc;m trần nối ống gi&oacute;.</p>\r\n\r\n<p>- Vị tr&iacute; được lắp đặt tr&ecirc;n tường, trần, chiều d&agrave;i tối đa c&oacute; thể l&ecirc;n đến 1,3m, với chiều d&agrave;i lớn hơn sẽ được gh&eacute;p bằng&nbsp;nhiều cửa nối tiếp nhau.</p>\r\n\r\n<p>- Được sử dụng l&agrave;m <a href=\"http://vitduct.com.vn/ag==/cua-gio\"><strong>cửa gi&oacute;</strong></a> cấp, cửa gi&oacute; hồi</p>\r\n\r\n<p>- C&aacute;c c&aacute;nh c&oacute; thể điều ch&igrave;nh lưu lượng gi&oacute; theo nhiều hướng...</p>\r\n\r\n<p>- Sẽ l&agrave; hai hay nhiều đợn gh&eacute;p lại khi k&iacute;ch thước lớn hơn 1200mm.</p>\r\n\r\n<p><strong>* Vật liệu</strong></p>\r\n\r\n<p>- Khung v&agrave; c&aacute;nh&nbsp; được l&agrave;m từ nh&ocirc;m định h&igrave;nh tăng độ cứng.</p>\r\n\r\n<p>- Được sơn&nbsp;tĩnh điện m&agrave;u trắng, m&agrave;u sắc kh&aacute;c theo y&ecirc;u cầu của Kh&aacute;ch h&agrave;ng .</p>\r\n\r\n<p><img alt=\"\" src=\"http://vitduct.com.vn/storage/app/public/uploads/images/cua-gio-kieu-khe-slot_result.jpg\" style=\"height:460px; width:1000px\" /></p>\r\n\r\n<p><img alt=\"\" src=\"http://vitduct.com.vn/storage/app/public/uploads/images/cua-gio-kieu-khe-slot-1_result.jpg\" style=\"height:1200px; width:947px\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>', 0, 0, 1, 1, 5, 4, 1, '0', '2016-04-11 17:15:24', '2019-12-16 00:25:32'),
(30, 'Cửa gió ngoài trời nan Z louver: V-WL_LCCC', 'Cửa gió nan Z chống hắt và chống côn trùng', '<h2>Cửa gi&oacute; ngo&agrave;i trời nan Z louver: V-WL_LCCC</h2>\r\n\r\n<p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;<img alt=\"\" src=\"http://vitduct.com.vn/storage/app/public/uploads/images/C%E1%BB%ADa%20gi%C3%B3%20nan%20Z%20k%C3%A8m%20l%C6%B0%E1%BB%9Bi%20ch%E1%BA%AFn%20c%C3%B4n%20tr%C3%B9ng%201_2(1).jpg\" style=\"height:267px; width:400px\" /><img alt=\"\" src=\"http://vitduct.com.vn/storage/app/public/uploads/images/C%E1%BB%ADa%20gi%C3%B3%20nan%20Z%20k%C3%A8m%20l%C6%B0%E1%BB%9Bi%20ch%E1%BA%AFn%20c%C3%B4n%20tr%C3%B9ng%201_1(1).jpg\" style=\"height:267px; width:400px\" /></p>\r\n\r\n<p><strong>* Đặc điểm sử dụng của <a href=\"http://vitduct.com.vn/ag==/cua-gio\">cửa gi&oacute; nan Z</a></strong></p>\r\n\r\n<p><strong>-&nbsp;</strong>Cửa gi&oacute; na Z (louver) của VITDUCT được sử dụng cho hệ th&ocirc;ng cấp gi&oacute; tươi hoặc thải gi&oacute;. N&oacute; c&oacute; thể bao gồm lưới chắn c&ocirc;n tr&ugrave;ng Inox ( sẽ c&oacute; th&ecirc;m k&yacute; hiệu LCCC) nếu kh&ocirc;ng bao gồm lưới chắn c&ocirc;n tr&ugrave;ng Inox ( sẽ kh&ocirc;ng c&oacute; th&ecirc;m k&yacute; hiệu LCCC ), để chống chịu được t&igrave;nh trạng m&ocirc;i trường đặc th&ugrave; của Việt Nam. Với kết cấu khung chắc chắn, đường n&eacute;t mạch lạc v&agrave; thẩm mỹ cao, loại cửa n&agrave;y rất ph&ugrave; hợp với c&aacute;c c&ocirc;ng tr&igrave;nh d&acirc;n dụng v&agrave; c&ocirc;ng tr&igrave;nh c&ocirc;ng nghiệp. Tiếp diện chuyển gi&oacute; xấp xỉ 40% tiết diện cửa.</p>\r\n\r\n<p>- Được sử dụng l&agrave;m cửa gi&oacute; cấp, cửa gi&oacute; hồi khi y&ecirc;u cầu mỹ thuật c&ocirc;ng tr&igrave;nh.</p>\r\n\r\n<p>- Đặc biệt c&oacute; khả năng tr&aacute;nh được nước mưa rất tốt thường được gắn ngo&agrave;i trời.</p>\r\n\r\n<p>- Được lắp tr&ecirc;n tường hoặc trực tiếp tr&ecirc;n đường ống gi&oacute;.</p>\r\n\r\n<p>- C&oacute; thể gắn k&egrave;m theo lười lọc bụi hoặc lưới chắn c&ocirc;n tr&ugrave;ng.</p>\r\n\r\n<p><strong>* Vật liệu v&agrave; m&agrave;u sắc.</strong></p>\r\n\r\n<p>- C&aacute;nh được l&agrave;m từ nh&ocirc;m định h&igrave;nh c&oacute; hết cấu m&oacute;c chắn nước mưa.</p>\r\n\r\n<p>- Khung được l&agrave; từ nh&ocirc;m định h&igrave;nh c&oacute; g&acirc;n tăng cứng v&agrave; gờ tạo khung mỹ thuật cao.</p>\r\n\r\n<p>- Được sơn tĩnh điện m&agrave;u trắng, m&agrave;u sắc kh&aacute;c theo y&ecirc;u cẩu của Kh&aacute;ch h&agrave;ng.</p>\r\n\r\n<p><strong>* Phụ kiệm đi k&egrave;m:</strong></p>\r\n\r\n<p>- Lưới chắn c&ocirc;n tr&ugrave;ng ( nếu kh&aacute;ch h&agrave;ng đặt h&agrave;ng): LCCC</p>\r\n\r\n<p>- Phin lọc bụi th&ocirc; ( nếu kh&aacute;ch h&agrave;ng đặt h&agrave;ng): FS</p>\r\n\r\n<p><img alt=\"\" src=\"http://vitduct.com.vn/storage/app/public/uploads/images/cua-gio-ngoai-troi-nan-Z_result.jpg\" style=\"height:553px; width:1000px\" /></p>\r\n\r\n<p>&nbsp; &nbsp; &nbsp; &nbsp;<img alt=\"\" src=\"http://vitduct.com.vn/storage/app/public/uploads/images/cua-gio-ngoai-troi-nan-Z-1_result.jpg\" style=\"height:1200px; width:873px\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>', 0, 0, 1, 1, 5, 4, 1, '0', '2016-04-11 17:17:54', '2019-12-16 01:16:21'),
(32, 'Cửa gió kiểu khe nan T: V-LG/T', 'Cửa gió nan thẳng một lớp', '<h2>Cửa gi&oacute; kiểu khe nan T: V-LG/T</h2>\r\n\r\n<p><img alt=\"\" src=\"http://vitduct.com.vn/storage/app/public/uploads/images/C%E1%BB%ADa%20gi%C3%B3%20nan%20T%201_1(1).jpg\" style=\"height:267px; width:400px\" /><img alt=\"\" src=\"http://vitduct.com.vn/storage/app/public/uploads/images/C%E1%BB%ADa%20gi%C3%B3%20nan%20T%201_2(1).jpg\" style=\"height:267px; width:400px\" /></p>\r\n\r\n<p><strong>* Đặc điểm sử dụng:</strong></p>\r\n\r\n<p>- Được sử dụng l&agrave;m cửa gi&oacute; cấp, cửa gi&oacute; hồi.</p>\r\n\r\n<p>- Thường được gắn tr&ecirc;n trần giả hoặc trực tiếp tr&ecirc;n đường ống gi&oacute;.</p>\r\n\r\n<p>- Cửa gi&oacute; dạng khe hẹp của VITDUCT được thiết kế đặc biệt cho c&aacute;c c&ocirc;ng tr&igrave;nh với thẩm mỹ cao như sảnh kh&aacute;ch sạn, ph&ograve;ng ngủ cao cấp. Thường được &aacute;p dụng với hệ thống th&ocirc;ng gi&oacute; - cấp nhiệt, hệ thống điều h&ograve;a &acirc;m trần nối ống gi&oacute;. Vị tr&iacute; lắp đặt tr&ecirc;n tường, trần hoặc s&agrave;n nh&agrave;. Chiều d&agrave;i tối đa của cửa l&agrave; 1,3m, với chiều d&agrave;i lớn hơn, sẽ được gh&eacute;p th&agrave;nh nhiều cửa nối tiếp.</p>\r\n\r\n<p><strong>* Vật liệu v&agrave; m&agrave;u sắc:</strong></p>\r\n\r\n<p>- Khung v&agrave; c&aacute;nh được l&agrave;m từ nh&ocirc;m định h&igrave;nh c&oacute; g&acirc;n tăng cứng va gờ tạo khung mỹ thuật cao.</p>\r\n\r\n<p>- t&ugrave;y theo mỹ thuật c&ocirc;ng tr&igrave;nh để chọn ra cửa c&oacute; kiểu nan th&iacute;ch hợp.</p>\r\n\r\n<p>- Được sơn&nbsp;tĩnh điện m&agrave;u trắng, m&agrave;u sắc kh&aacute;c theo y&ecirc;u cầu của kh&aacute;ch h&agrave;ng.</p>\r\n\r\n<p><img alt=\"\" src=\"http://vitduct.com.vn/storage/app/public/uploads/images/cua-gio-kieu-khe-nan-T_result.jpg\" style=\"height:730px; width:1000px\" /></p>\r\n\r\n<p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;<img alt=\"\" src=\"http://vitduct.com.vn/storage/app/public/uploads/images/cua-gio-kieu-khe-nan-T-1_result.jpg\" style=\"height:1200px; width:901px\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>', 0, 0, 1, 1, 5, 4, 1, '0', '2016-04-11 17:21:41', '2019-12-16 01:23:53'),
(33, 'Cửa gió nam thẳng hai lớp ( Nan bầu dục): V-DDG', 'Cửa gió kiểu nan lưới 2 lớp', '<h2>Cửa gi&oacute; nam thẳng hai lớp ( Nan bầu dục): V-DDG</h2>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt=\"\" src=\"http://vitduct.com.vn/storage/app/public/uploads/images/C%E1%BB%ADa%20gi%C3%B3%20nan%20b%E1%BA%A7u%20d%E1%BB%A5c%202%20l%E1%BB%9Bp%201_2(2).jpg\" style=\"height:267px; width:400px\" /><img alt=\"\" src=\"http://vitduct.com.vn/storage/app/public/uploads/images/C%E1%BB%ADa%20gi%C3%B3%20nan%20b%E1%BA%A7u%20d%E1%BB%A5c%202%20l%E1%BB%9Bp%201_1(2).jpg\" style=\"height:267px; width:400px\" /></p>\r\n\r\n<p><big><strong>* Đặc điểm sử dụng</strong></big></p>\r\n\r\n<p><big>- Được sử dụng l&agrave;m cửa gi&oacute; cấp, cửa gi&oacute; hồi c&oacute; k&iacute;ch thước lớn</big></p>\r\n\r\n<p><big>- Nam bầu dục c&oacute; thể điều chỉnh hướng gi&oacute; theo y&ecirc;u cầu.</big></p>\r\n\r\n<p><big>- Loại cừa n&agrave;y thường được lắp tr&ecirc;n trần, v&aacute;ch tường hoặc lắp trực tiếp v&agrave;o ống gi&oacute;. Ch&uacute;ng t&ocirc;i c&oacute; hai loại cửa gi&oacute; nam bầu dục v&agrave; nan cong.</big></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><big><strong>* Vật liệu v&agrave; m&agrave;u sắc:</strong></big></p>\r\n\r\n<p><big>- Khung v&agrave; c&aacute;nh&nbsp;được l&agrave;m từ nh&ocirc;m định h&igrave;nh, tr&ecirc;n c&oacute; g&acirc;n tăng cứng v&agrave; gờ tạo khung thẩm mỹ cao.</big></p>\r\n\r\n<p><big>- Được sơn bằng d&acirc;y chuyền sơn tĩnh điện, m&agrave;u sắc kh&aacute;c theo y&ecirc;u cầu của Kh&aacute;ch h&agrave;ng</big>.</p>\r\n\r\n<p><img alt=\"\" src=\"http://vitduct.com.vn/storage/app/public/uploads/images/cua-nan-gio-thang-2-lop_result.jpg\" style=\"height:941px; width:971px\" /></p>\r\n\r\n<p><img alt=\"\" src=\"http://vitduct.com.vn/storage/app/public/uploads/images/cua-nan-gio-thang-2-lop-1_result.jpg\" style=\"height:814px; width:1000px\" /></p>\r\n\r\n<p><img alt=\"\" src=\"http://vitduct.com.vn/storage/app/public/uploads/images/cua-nan-gio-thang-2-lop-2_result.jpg\" style=\"height:477px; width:1000px\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>', 0, 0, 1, 1, 5, 4, 1, '0', '2016-04-11 17:23:11', '2019-12-13 02:50:19'),
(34, 'Cửa gió kiểu hộp', 'Cửa gió kiểu hộp gắn trần', '<p>&nbsp;</p>\r\n\r\n<p><img alt=\"\" src=\"http://vitduct.com.vn/storage/app/public/uploads/images/2368Hop.jpg\" style=\"height:188px; width:268px\" /></p>\r\n\r\n<p><strong>* Đặc điểm sử dụng</strong></p>\r\n\r\n<p>- Được sử dụng l&agrave;m cửa gi&oacute; cấp, cửa gi&oacute; hồi</p>\r\n\r\n<p>- Th&iacute;ch hợp lắp đặt với tất cả c&aacute;c loại trần giả</p>\r\n\r\n<p>- Khung v&agrave; l&otilde;i ri&ecirc;ng biệt, dễ d&agrave;ng th&aacute;o lắp v&agrave; bảo tr&igrave;</p>\r\n\r\n<p>- C&oacute; thể l&agrave; h&igrave;nh vu&ocirc;ng hoặc h&igrave;nh chữ nhật, t&ugrave;y v&agrave;o thiết kế. T&ugrave;y vị tr&iacute; lắp đặt c&oacute; thể chọn loại thổi 2 hoặc 4 hướng thổi.</p>\r\n\r\n<p><strong>* Vật liệu</strong></p>\r\n\r\n<p>- Khung được l&agrave;m từ Nh&ocirc;m định h&igrave;nh, tr&ecirc;n c&oacute; g&acirc;n tăng cứng v&agrave; ke g&oacute;c để tạo g&oacute;c vu&ocirc;ng cho to&agrave;n bộ cửa.</p>\r\n\r\n<p>- C&aacute;nh được l&agrave;m từ Nh&ocirc;m định h&igrave;nh, tr&ecirc;n c&aacute;nh c&oacute; g&acirc;n trợ lực để tăng cứng.</p>\r\n\r\n<p>- Được sơn bằng d&acirc;y chuyền sơn tĩnh điện theo c&ocirc;ng nghệ H&agrave;n Quốc v&agrave; Nhật Bản. M&agrave;u sắc đa dạng.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>', 0, 0, 1, 1, 5, 4, 1, '0', '2016-04-11 17:24:39', '2019-07-13 01:18:59'),
(35, 'Cửa gió tròn: V-RD', 'Cửa gió tròn gắn trần', '<h2>Cửa gi&oacute; tr&ograve;n: V-RD</h2>\r\n\r\n<p><img alt=\"\" src=\"http://vitduct.com.vn/storage/app/public/uploads/images/420Cua%20gio%20tron.jpg\" style=\"height:200px; width:200px\" /></p>\r\n\r\n<p><strong>* Đặc điểm sử dụng</strong></p>\r\n\r\n<p>- Được sử dụng l&agrave;m cửa gi&oacute; cấp, cửa gi&oacute; hồi</p>\r\n\r\n<p>- C&oacute; thể gắn k&egrave;m với van VCD hoặc c&oacute; thể gắn trực tiếp trần đảm bảo t&iacute;nh thẩm mỹ cao.</p>\r\n\r\n<p>- Loại cửa n&agrave;y được sử dụng cho hệ thống cấp nhiệt, th&ocirc;ng gi&oacute; v&agrave; điều h&ograve;a. Gi&oacute; được l&agrave;n tỏa theo luồng tr&ograve;n song song đảm bảo giảm thiểu tổn thất lưu lượng v&agrave; &aacute;p suất. Phần l&otilde;i cửa c&oacute; thể dễ d&agrave;ng th&aacute;o rời cho việc vệ sinh v&igrave; n&oacute; kết nối với v&agrave;nh cửa bời một trục v&iacute;t ở trung t&acirc;m cửa.</p>\r\n\r\n<p><strong>* Vật liệu</strong></p>\r\n\r\n<p>- Khung v&agrave; c&aacute;nh&nbsp; được l&agrave;m từ nh&ocirc;m định h&igrave;nh hoặc th&eacute;p.</p>\r\n\r\n<p>- Được sơn&nbsp;tĩnh điện m&agrave;u trắng, m&agrave;u sắc kh&aacute;c theo y&ecirc;u cầu của Kh&aacute;ch h&agrave;ng .</p>\r\n\r\n<p><img alt=\"\" src=\"http://vitduct.com.vn/storage/app/public/uploads/images/cua-gio-tron_result.jpg\" style=\"height:300px; width:1000px\" /></p>\r\n\r\n<p><img alt=\"\" src=\"http://vitduct.com.vn/storage/app/public/uploads/images/cua-gio-tron-1_result.jpg\" style=\"height:758px; width:1000px\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>', 0, 0, 1, 1, 5, 4, 1, '0', '2016-04-11 17:27:58', '2019-12-16 01:03:32');
